Actions.Action:
(if (typGetData &unidKorolovShipping; 'level)
	(rpgDockServices gPlayerShip {
		checkMilitaryID: True
		})
	(scrShowScreen gScreen &dsRPGMessage; {
		desc: (scrTranslate gScreen 'descNoDockServicesAccess)
		})
	)

(block (
	;	Get a list of freighter types and names so we can sort alphabetically
	(shipList (map (korFreighterTypes) theType {ship:theType name:(shpGetClassName theType 1)}))
	)
	(scrShowScreen gScreen "ShipData" {
		shipType: 'freighter
		shipList: (map (sort shipList 'ascending 'name) theEntry (@ theEntry 'ship))
		})
	)

ServiceRecord.OnScreenInit:
(block (
	(shipList (sort (map (korFreighterTypes) theType {
			class:theType
			name:(shpGetClassName theType 0x080)
			level:(max (typGetStaticData theType 'korolovMinLevel) 1)
			})
		'ascending 'name))
	(levelList (map (make 'sequence 3) level (filter shipList theShip (= (@ theShip 'level) level))))
	)
	(scrSetData gScreen 'nameList
		(join (map levelList theList (join (map theList theShip (@ theShip 'name)) "\n")) "\n\n")
		)
	(scrSetData gScreen 'levelList levelList)
	)

Language.Text:
(cat "\"Korolov Shipping needs more pilots with your skills and determination. "
	"Please come back to us, or any other Korolov station, if you want to earn a bit of money.\""
	)
